#! /bin/bash

PLUGIN_NAME='App2'
APP_KIND='workflow'
STRING_SIZE=256
MAIN_PAGE='list_waiting_for_your_action'
./redis_create_app.sh $PLUGIN_NAME $APP_KIND $STRING_SIZE $MAIN_PAGE
./redis_config_workflow.sh $PLUGIN_NAME 'enable' 'disable' "external_baseurl|https://panasonic.briode.com/Generic"

# Visibility
./redis_config_db_visibility.sh $PLUGIN_NAME 'acl'

# Report
./redis_config_report.sh $PLUGIN_NAME 'disable'

MAINVIEW_COLUMNS='subject_id created_at creator_id summary assignee Requested_by Ship_to'
SEARCHVIEW_COLUMNS='id created_at creator_id Date Requested_by Sheet_No Ship_to'
